import { UserAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { useEffect } from "react";
import { FcGoogle } from "react-icons/fc";
// import { GoogleButton } from "react-google-button";y
import classes from "../components/css/SignInPage.module.css";

const SignInPage = () => {
  const { googleSignIn, user } = UserAuth();
  const navigate = useNavigate();

  const handleGoogleSignIn = async () => {
    try {
      await googleSignIn();
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    if (user != null) {
      navigate("/account");
    }
  }, [navigate, user]);

  return (
    <div className="container centre">
      <button onClick={handleGoogleSignIn} className={classes.button}>
        <div className={classes.buttonLayout}>
          <div className={classes.gridLogo}>
            <FcGoogle className={classes.logo} />
          </div>
          <div className={classes.gridText}>Sign in with Google</div>
        </div>
      </button>
      {/* <GoogleButton onClick={handleGoogleSignIn}/> */}
    </div>
  );
};

export default SignInPage;
